import React, { useState } from 'react';
import Listing from './components/Listing';
import './App.css';

function App() {
  const [movieName, setMovieName] = useState("creed-iii")
  const [movies, setMovies] = useState([])

  const handleChange = (event) => {
    setMovieName(event.target.value)
  }

  const handleClick = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/' + movieName)
      const m = await response.json()
      console.log(m)
      setMovies(m)
    } catch (err) {
      console.error(err)
    }
  }

  return (
    <div className="App">
      <input placeholder='Enter movie name' onChange={handleChange}/>
      <button onClick={handleClick}>Find Tickets</button>
      <div>
        {movies.length > 0 && movies.map((movie) => {
          movie.formatted_time_price_list.map((formatted_time_price_combo) => {
            formatted_time_price_combo.time_prices_combos.map((combo) => {
              <Listing theater={movie.theater} addy={movie.addy} time={combo.time} price={combo.prices[0]}/>
            })
          })
        })}
      </div>
    </div>
  );
}

export default App;
