import React, { useState } from 'react';
import Listing from 'client\src\components\Listing.jsx';
import './App.css';

function App() {
  const [name, setName] = useState(null)
  const [movies, setMovies] = useState(null)

  const handleChange = (event) => {
    setName(event.target.value)
    setMovies(null)
  }

  const handleClick = async () => {
    try {
      const m = await fetch('http://localhost:3001/api/' + {name})
      setMovies(m)
    } catch (err) {
      console.error(err)
    }
  }

  return (
    <div className="App">
      <input placeholder='Enter movie name' onChange={handleChange}/>
      <button onClick={handleClick}>Find Tickets</button>
      {movies.length > 0 && movies.map((movie) => {
        movie.formatted_time_price_list.map((formatted_time_price_combo) => {
          formatted_time_price_combo.time_prices_combos.map((combo) => {
            <Listing theater={movie.theater} addy={movie.addy} time={combo.time} price={combo.prices[0]}/>
          })
        })
      })}
    </div>
  );
}

export default App;
